                    grid[h][w - 1] = grid[h][w]
            sw -= 1
        elif d == 'R':
            for h in range(1, H + 1):
                for w in range(sw, W + 1):
                    grid[h][w] = grid[h][w - 1]
            sw += 1
        elif d == 'U':
            for h in range(1, sh):
