def matrix_power_mod(matrix, p, mod):
    """
    Computes the matrix power of matrix raised to the power of p modulo mod.
    """
    result = [[0] * len(matrix) for _ in range(len(matrix))]
    for i in range(len(matrix)):
        result[i][i] = 1
    base = matrix
    while p > 0:
        if p % 2 == 1:
            result = matrix_multiply_mod(result, base, mod)
        base = matrix_multiply_mod(base, base, mod)
        p //= 2
    return result

def matrix_multiply_mod(A, B, mod):
    """
    Multiplies two matrices A and B modulo mod.
    """
    result = [[0] * len(B[0]) for _ in range(len(A))]
    for i in range(len(A)):
        for j in range(len(B[0])):
            for k in range(len(B)):
                result[i][j] = (result[i][j] + A[i][k] * B[k][j]) % mod
    return result

