def is_good_string(N, A):
    """
    Determines if there exists a good string of length N based on the given sequence A.

    :param N: Length of the sequence A
    :param A: List of integers representing the sequence A
    :return: True if a good string exists, False otherwise
    """
    # Check if the sequence A contains no 0
    if 0 in A:
        return False

    # Check if the sequence A is already a good string
    if all(A[i] == 1 for i in range(N)):
        return True

    # Check if the sequence A can be transformed into a good string by performing the given operations
    for i in range(N):
        if A[i] == 0:
            continue
        if A[i + 1] == 0 and A[i + 2] == 0:
            A[i] = 1
            A[i + 1] = 1
            break
        if A[i + 2] == 0 and A[i + 1] == 0:
            A[i] = 1
            A[i + 2] = 1
            break

    return all(A[i] == 1 for i in range(N))

