from functools import reduce

def f(x):
    """
    Computes the value of f(x) for a positive rational number x.
    x is represented as a fraction P/Q where P and Q are coprime positive integers.
    f(x) is defined as P * Q.
    """
    return x.numerator * x.denominator

def is_good_sequence(sequence, A):
    """
    Checks if a sequence is a good sequence based on the given A.
    """
    for i in range(len(sequence) - 1):
        if f(sequence[i] / sequence[i + 1]) != A[i]:
            return False
    return gcd(*sequence) == 1

def sum_of_scores(N, A):
    """
    Computes the sum of the scores of all good sequences.
    """
    from itertools import product
    from fractions import Fraction

