            for q in range(1, 1001):
                if gcd(p, q) == 1:
                    for n in range(1, 1001):
                        for s in product(range(1, 1001), repeat=n):
                            if is_good_sequence(s, A):
                                yield s

    total_sum = 0
    for seq in generate_sequences():
        total_sum = (total_sum + reduce(lambda x, y: x * y, seq)) % 998244353
    return total_sum

