def main():
    """
    Reads input from standard input, processes the data to generate a graph and weights,
    and then processes Q queries to determine the minimum weight of a path between two vertices.
    """
    import sys
    input = sys.stdin.read().split()

    N = int(input[0])
    weights = list(map(int, input[1:N+1]))
    edges = []
    for i in range(N):
        L, R = map(int, input[N+2*i+1:N+2*i+3])
        edges.append((L, R))

    Q = int(input[N*2+2])
    queries = []
    for i in range(Q):
        s, t = map(int, input[N*2+3+i*N+2:N*2+3+i*N+4])
        queries.append((s, t))

    # Build the graph
    graph = {i: set() for i in range(1, N+1)}
    for L, R in edges:
        for i in range(L, R+1):
            for j in range(i+1, R+1):
                graph[i].add(j)
                graph[j].add(i)

    # Process queries
    for s, t in queries:
        if s == t:
            print(-1)
            continue
        min_weight = float('inf')
        visited = [False] * (N+1)
        stack = [(s, 0)]
        while stack:
            current, weight = stack.pop()
            if current == t:
                min_weight = min(min_weight, weight)
                continue
            visited[current] = True
            for neighbor in graph[current]:
                if not visited[neighbor]:
                    stack.append((neighbor, weight + weights[neighbor-1]))
        print(min_weight if min_weight != float('inf') else -1)

