    def is_valid_parenthesis(s):
        stack = []
        for char in s:
            if char == '(':
                stack.append(char)
            elif char == ')':
                if not stack or stack[-1] != '(':
                    return False
                stack.pop()
        return not stack

    # Function to reverse a substring of a string
    def reverse_substring(s, l, r):
        return s[:l] + s[l:r+1][::-1] + s[r+1:]

    # Function to generate all valid parenthesis sequences by reversing substrings
    def generate_sequences(s):
        if is_valid_parenthesis(s):
            return {s}

        sequences = set()
        n = len(s)
        for i in range(n):
            for j in range(i, n):
                if is_valid_parenthesis(s[i:j+1]):
                    new_s = reverse_substring(s, i, j)
                    sequences.update(generate_sequences(new_s))
        return sequences

    # Generate all valid sequences
    sequences = generate_sequences(S)

    # Return the number of distinct sequences modulo 998244353
    return len(sequences) % 998244353

