def count_ways(N, S):
    """
    Counts the number of ways to partition the vertices into N pairs such that the final graph is strongly connected.

    :param N: Number of vertices
    :param S: String representing the color of each vertex
    :return: Number of ways to partition the vertices into N pairs such that the final graph is strongly connected, modulo 998244353
    """
    # Initialize the adjacency list
    adj = [[] for _ in range(2 * N)]

    # Build the graph
    for i in range(2 * N):
        if S[i] == 'W':
            adj[i].append(i + 1)
        else:
            adj[i].append(i - 1)

    # Function to perform DFS and check if the graph is strongly connected
    def is_strongly_connected(v, visited, adj):
        visited[v] = True
        for neighbor in adj[v]:
            if not visited[neighbor]:
                is_strongly_connected(neighbor, visited, adj)

    # Check if the graph is strongly connected
    visited = [False] * (2 * N)
    is_strongly_connected(0, visited, adj)

    # If the graph is not strongly connected, return 0
    if not all(visited):
        return 0

    # Count the number of ways to partition the vertices into N pairs
    count = 0
    for i in range(0, 2 * N, 2):
        for j in range(i + 2, 2 * N, 2):
            if S[i] == 'W' and S[j] == 'B':
                count += 1
            elif S[i] == 'B' and S[j] == 'W':
                count += 1

    return count

