class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)
        dp = [0] * (n + 1)
        dp[0] = 1

        for i in range(n):
            for j in range(i, -1, -1):
                if nums[i] >= nums[j]:
                    dp[i + 1] += dp[j]
                else:
                    dp[i + 1] += dp[j] - (nums[i] - nums[j] - 1) * (i - j)
                    dp[i + 1] = min(dp[i + 1], k + 1)
                    break

        return dp[n]

