    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)
        dp = [0] * (n + 1)
        ans = 0

        for i in range(n):
            dp[i + 1] = dp[i] + 1
            for j in range(i, -1, -1):
                if nums[j] > nums[i]:
                    dp[i + 1] -= 1
                else:
                    break
            dp[i + 1] += min(k, dp[i] - dp[j])
            ans += dp[i + 1]

