class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        n = len(nums)
        graph = [[] for _ in range(n)]
        for u, v, _ in edges:
            graph[u].append((v, nums[v]))
            graph[v].append((u, nums[u]))

        def dfs(node, parent):
            nonlocal max_len, min_len, max_count, min_count
            max_len = max(max_len, 1)
            min_len = 1
            max_count = 0
            min_count = 1

            for child, child_val in graph[node]:
                if child == parent:
                    continue
                child_len, child_min_len, child_max_count, child_min_count = dfs(child, node)
                if child_val != nums[node]:
                    if child_len > 0:
                        max_len = max(max_len, child_len + min_len)
                        min_len = 1
                    if child_max_count > 0:
                        min_count = min(min_count, child_max_count)
                        max_count = max(max_count, child_max_count)
                else:
                    if child_len > 0:
                        max_len = max(max_len, child_len + min_len - 1)
                        min_len = 1
                    if child_max_count > 0:
                        min_count = min(min_count, child_max_count - 1)
                        max_count = max(max_count, child_max_count - 1)

            if max_len > 0:
                max_count += 1
            if min_count > 0:
                min_count += 1

            return max_len, min_len, max_count, min_count

        max_len, min_len, max_count, min_count = 0, 0, 0, 0
        dfs(0, -1)
        return [max_len, min_count]
