class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        from collections import defaultdict, deque

        graph = defaultdict(list)
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        def dfs(node, parent):
            """
            :type node: int
            :type parent: int
            :rtype: int, int
            """
            max_length = 0
            max_length_2 = 0
            unique_count = 0

            for neighbor, length in graph[node]:
                if neighbor == parent:
                    continue

                length, unique = dfs(neighbor, node)

                if nums[node] != nums[neighbor]:
                    unique_count += 1
                    if length > max_length:
                        max_length_2 = max_length
                        max_length = length
                    elif length > max_length_2:
                        max_length_2 = length
                else:
                    if length > max_length:
                        max_length_2 = max_length
                        max_length = length
                    elif length > max_length_2:
                        max_length_2 = length

            if unique_count == 0:
                return 0, 0

            return max_length + 1, max_length_2 + 1

        max_length = 0
        max_length_2 = 0
        unique_count = 0

        for node in range(len(nums)):
            length, unique = dfs(node, -1)

            if unique > 1:
                unique_count += 1
                if length > max_length:
                    max_length_2 = max_length
                    max_length = length
                elif length > max_length_2:
                    max_length_2 = length
            else:
                if length > max_length:
                    max_length_2 = max_length
                    max_length = length
                elif length > max_length_2:
                    max_length_2 = length

        return [max_length, unique_count]
