class Solution:
    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        """
        Finds the shortest substring in s that matches the pattern p.

        :param s: The input string.
        :param p: The pattern string containing exactly two '*' characters.
        :return: The length of the shortest matching substring, or -1 if no such substring exists.
        """
        def is_match(i, j):
            """
            Checks if the substring s[i:j] matches the pattern p.

            :param i: Starting index of the substring in s.
            :param j: Ending index of the substring in s.
            :return: True if the substring matches the pattern, False otherwise.
            """
            star1, star2 = False, False
            while i < j:
                if p[i] == '*':
                    if star1:
                        i += 1
                    elif star2:
                        j -= 1
                    else:
                        star1 = True
                        i += 1
                elif p[i] == '?':
                    i += 1
                    j -= 1
                elif s[i] != p[i]:
                    return False
                else:
                    i += 1
                    j -= 1
            return True

        n, m = len(s), len(p)
        if m != 2 or p[0] == '*' or p[1] == '*':
            return -1

        min_len = float('inf')
        for i in range(n):
            for j in range(i, n + 1):
                if is_match(i, j):
                    min_len = min(min_len, j - i)
        return min_len if min_len != float('inf') else -1
