    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        """
        Finds the shortest substring in s that matches the pattern p.

        Parameters:
        s (str): The input string.
        p (str): The pattern string containing exactly two '*' characters.

        Returns:
        int: The length of the shortest matching substring, or -1 if no such substring exists.
        """
        def is_match(i, j):
            """
            Checks if the substring s[i:j] matches the pattern p.

            Parameters:
            i (int): The starting index of the substring.
            j (int): The ending index of the substring.

            Returns:
            bool: True if the substring matches the pattern, False otherwise.
            """
            k = 0
            while i < j:
                if p[k] == '*':
                    if k == 0:
                        if p[1] == '*':
                            return is_match(i, j)
                        else:
                            return is_match(i + 1, j)
                    elif k == 1:
                        if p[0] == '*':
                            return is_match(i, j)
                        else:
                            return is_match(i + 1, j)
                    else:
                        if p[k - 1] == '*':
                            return is_match(i, j)
                        else:
                            return is_match(i + 1, j)
                elif p[k] == s[i]:
                    i += 1
                    k += 1
                else:
                    return False
            return k == len(p)

        n = len(s)
        m = len(p)
        if m != 2:
            return -1

        i, j = 0, 0
        while i < n:
            if p[0] == '*':
                if is_match(i, n):
                    return n - i
                i += 1
            elif p[1] == '*':
                if is_match(i, n):
                    return n - i
                j += 1
            elif s[i] == p[0]:
                i += 1
            elif s[i] == p[1]:
                j += 1
            else:
                return -1

        return -1

