class Solution:
    def minimumIncrements(self, nums: List[int], target: List[int]) -> int:
        """
        Returns the minimum number of operations required to make each element in target a multiple of at least one element in nums.
        """
        # Create a frequency dictionary for each element in target
        target_freq = {}
        for num in target:
            if num not in target_freq:
                target_freq[num] = 0
            target_freq[num] += 1

        # Sort the elements in target in descending order of frequency
        sorted_target = sorted(target_freq.keys(), key=lambda x: target_freq[x], reverse=True)

        # Initialize the result counter
        result = 0

        # Iterate over the sorted target elements
        for num in sorted_target:
            # Check if the current element is a multiple of any element in nums
            if all(num % n == 0 for n in nums):
                # If it is, decrement the frequency of the current element in target
                target_freq[num] -= 1
                # If the frequency becomes zero, remove the element from the dictionary
                if target_freq[num] == 0:
                    del target_freq[num]
            else:
                # If it is not, increment the current element in nums by 1
                for n in nums:
                    if num % n == 0:
                        nums[num // n] += 1
                        result += 1
                        break

        return result
