class Solution:
    def zigzagTraversal(self, grid: List[List[int]]) -> List[int]:
        """
        :type grid: List[List[int]]
        :rtype: List[int]
        """
        result = []
        m, n = len(grid), len(grid[0])
        row, col = 0, 0
        direction = 1  # 1 for right, -1 for left

        while row < m and col < n:
            result.append(grid[row][col])
            if direction == 1:
                if col == n - 1 or (row + 1 < m and grid[row + 1][col + 1] == 0):
                    direction = -1
                    if row + 1 < m:
                        row += 1
                    else:
                        col += 1
                else:
                    col += 1
            else:
                if row == 0 or (col - 1 >= 0 and grid[row - 1][col - 1] == 0):
                    direction = 1
                    if col - 1 >= 0:
                        col -= 1
                    else:
                        row += 1
                else:
                    col -= 1

        return result
