class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        """
        :type nums: List[int]
        :type x: int
        :type k: int
        :rtype: int
        """
        n = len(nums)
        if n < k * x:
            return -1

        prefix_sum = [0] * (n + 1)
        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]

        min_operations = float('inf')
        for i in range(n - k * x + 1):
            target = prefix_sum[i + k * x] - prefix_sum[i]
            if target == 0:
                min_operations = min(min_operations, n - i - k * x)
                break
            elif target < 0:
                break
            else:
                for j in range(i + k * x, n):
                    if prefix_sum[j + 1] - prefix_sum[j] == target:
                        min_operations = min(min_operations, n - j - k * x)
                        break
                else:
                    break

        return min_operations if min_operations != float('inf') else -1
