from collections import defaultdict
from bisect import bisect_left, bisect_right

class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        """
        :type nums: List[int]
        :type x: int
        :type k: int
        :rtype: int
        """
        n = len(nums)
        if n < k * x:
            return -1

        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        dp = defaultdict(int)
        for i in range(n + 1):
            for j in range(i, n + 1):
                subarray_sum = prefix_sum[j] - prefix_sum[i]
                if subarray_sum == x:
                    dp[i] = min(dp.get(i, float('inf')), j - i)

        min_operations = float('inf')
        for i in range(n + 1):
            for j in range(i, n + 1):
                subarray_sum = prefix_sum[j] - prefix_sum[i]
                if subarray_sum == x:
                    start = bisect_left(dp, j - i)
                    if start < len(dp):
                        min_operations = min(min_operations, dp[start] + j - i)

        return min_operations if min_operations != float('inf') else -1
