class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        """
        :type n: int
        :type edges: List[List[int]]
        :type threshold: int
        :rtype: int
        """
        graph = [[] for _ in range(n)]
        for a, b, w in edges:
            graph[a].append((b, w))
            graph[b].append((a, w))

        def dfs(node, parent, visited, weights):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor == parent:
                    continue
                if not visited[neighbor]:
                    weights[neighbor] = max(weights[node], weight)
                    dfs(neighbor, node, visited, weights)

        min_max_weight = float('inf')
        for node in range(n):
            if not visited[node]:
                visited = [False] * n
                weights = [0] * n
                weights[node] = 0
                dfs(node, -1, visited, weights)
                for weight in weights:
                    if weight > threshold:
                        return -1
                max_weight = max(weights)
                min_max_weight = min(min_max_weight, max_weight)
        return min_max_weight if min_max_weight != float('inf') else -1
