from collections import defaultdict
from heapq import heappush, heappop

class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        """
        :type n: int
        :type edges: List[List[int]]
        :type threshold: int
        :rtype: int
        """
        graph = defaultdict(list)
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        def dfs(node, parent, visited, min_weight):
            visited.add(node)
            for neighbor, weight in graph[node]:
                if neighbor != parent and neighbor not in visited:
                    if weight > min_weight:
                        min_weight = weight
                    min_weight = dfs(neighbor, node, visited, min_weight)
            return min_weight

        def bfs(node, parent, visited, min_weight):
            visited.add(node)
            queue = [(node, min_weight)]
            while queue:
                node, min_weight = heappop(queue)
                for neighbor, weight in graph[node]:
                    if neighbor != parent and neighbor not in visited:
                        if weight > min_weight:
                            min_weight = weight
                        heappush(queue, (neighbor, min_weight))
                        visited.add(neighbor)
            return min_weight

        def get_min_weight(node, parent, visited, min_weight):
            if node == 0:
                return dfs(node, parent, visited, min_weight)
            else:
                return bfs(node, parent, visited, min_weight)

        min_weight = float('inf')
        for node in range(n):
            visited = set()
            min_weight = min(min_weight, get_min_weight(node, -1, visited, min_weight))

        return min_weight if min_weight != float('inf') else -1
