class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        def dfs(node, parent, graph, visited, weights):
            visited[node] = True
            for neighbor in graph[node]:
                if neighbor != parent:
                    weights[node] = max(weights[node], weights[neighbor])
                    dfs(neighbor, node, graph, visited, weights)

        def find_min_max_weight(n, edges, threshold):
            graph = [[] for _ in range(n)]
            for u, v, w in edges:
                graph[u].append((v, w))
                graph[v].append((u, w))

            min_max_weights = [float('inf')] * n
            for node in range(n):
                if not min_max_weights[node] == float('inf'):
                    continue
                visited = [False] * n
                weights = [0] * n
                weights[node] = 0
                dfs(node, -1, graph, visited, weights)
                if not all(weights[i] <= threshold for i in range(n)):
                    continue
                min_max_weights[node] = max(weights)

            return min(min_max_weights)

        return find_min_max_weight(n, edges, threshold)
