    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        def dfs(node, parent, graph, visited, min_weights):
            visited[node] = True
            for neighbor in graph[node]:
                if neighbor != parent:
                    dfs(neighbor, node, graph, visited, min_weights)
            min_weights[node] = max(min_weights[node], max(min_weights[neighbor] for neighbor in graph[node] if neighbor != parent))

        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        min_weights = [0] * n
        for i in range(n):
            if not min_weights[i]:
                visited = [False] * n
                dfs(i, -1, graph, visited, min_weights)

        min_weights = [w for w in min_weights if w]
        min_weights.sort()

        for i in range(len(min_weights)):
            for j in range(i + 1, len(min_weights)):
                if min_weights[j] - min_weights[i] <= threshold:
                    for u in range(n):
                        for v in range(n):
                            if u != v and min_weights[u] + min_weights[v] <= min_weights[i] + min_weights[j]:
                                return min_weights[j]
        return -1
