from collections import defaultdict, deque
from heapq import heappush, heappop

class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        graph = defaultdict(list)
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        def bfs(start):
            visited = [False] * n
            visited[start] = True
            queue = deque([(start, 0)])
            while queue:
                node, weight = queue.popleft()
                for neighbor, neighbor_weight in graph[node]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        heappush(queue, (neighbor, weight + neighbor_weight))
            return weight

        def dfs(node, visited, current_weight, max_weight):
            if current_weight > max_weight:
                return float('inf')
            if node == 0:
                return max_weight
            if visited[node]:
                return max_weight
            visited[node] = True
            min_weight = float('inf')
            for neighbor, neighbor_weight in graph[node]:
                min_weight = min(min_weight, dfs(neighbor, visited, current_weight + neighbor_weight, max_weight))
            visited[node] = False
            return min_weight

        min_max_weight = float('inf')
        for i in range(n):
            if bfs(i) < threshold:
                continue
            for j in range(n):
                if i == j:
                    continue
                for k in range(n):
                    if i == k or j == k:
                        continue
                    for neighbor, neighbor_weight in graph[i]:
                        if neighbor == j or neighbor == k:
                            continue
                        for neighbor2, neighbor2_weight in graph[j]:
                            if neighbor2 == i or neighbor2 == k:
                                continue
                            for neighbor3, neighbor3_weight in graph[k]:
                                if neighbor3 == i or neighbor3 == j:
                                    continue
                                max_weight = max(bfs(i), bfs(j), bfs(k))
                                min_weight = dfs(i, [False] * n, neighbor_weight, max_weight)
                                min_weight = min(min_weight, dfs(j, [False] * n, neighbor2_weight, max_weight))
                                min_weight = min(min_weight, dfs(k, [False] * n, neighbor3_weight, max_weight))
                                min_max_weight = min(min_max_weight, min_weight)
        return min_max_weight if min_max_weight != float('inf') else -1
