class Solution:
    def minMaxSubarraySum(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        def helper(nums, k, start, end, memo):
            if (k, start, end) in memo:
                return memo[(k, start, end)]

            if k == 0:
                return 0

            if start == end:
                return nums[start]

            min_val = float('inf')
            max_val = float('-inf')
            for i in range(start, end - k + 2):
                min_val = min(min_val, nums[i])
                max_val = max(max_val, nums[i])

            memo[(k, start, end)] = max_val + min_val + helper(nums, k - 1, start + 1, end, memo)
            return memo[(k, start, end)]

        return helper(nums, k, 0, len(nums), {})
