class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def dfs(i, j, direction, sequence, length):
            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 1:
                return 0

            if sequence == 0:
                sequence = 2
                length = 1
            elif sequence == 1:
                sequence = 0
                length += 1
            elif sequence == 2:
                sequence = 0
                length += 1
            elif sequence == 3:
                sequence = 0
                length += 1

            if direction == 0:  # top-left to bottom-right
                if i + 1 < len(grid) and j + 1 < len(grid[0]) and grid[i + 1][j + 1] == 2:
                    return dfs(i + 1, j + 1, 0, sequence, length)
                else:
                    return length
            elif direction == 1:  # bottom-right to top-left
                if i - 1 >= 0 and j - 1 >= 0 and grid[i - 1][j - 1] == 2:
                    return dfs(i - 1, j - 1, 1, sequence, length)
                else:
                    return length
            elif direction == 2:  # top-right to bottom-left
                if i + 1 < len(grid) and j - 1 >= 0 and grid[i + 1][j - 1] == 2:
                    return dfs(i + 1, j - 1, 2, sequence, length)
                else:
                    return length
            elif direction == 3:  # bottom-left to top-right
                if i - 1 >= 0 and j + 1 < len(grid[0]) and grid[i - 1][j + 1] == 2:
                    return dfs(i - 1, j + 1, 3, sequence, length)
                else:
                    return length

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, dfs(i, j, 0, 0, 1))
                    max_length = max(max_length, dfs(i, j, 1, 0, 1))
                    max_length = max(max_length, dfs(i, j, 2, 0, 1))
                    max_length = max(max_length, dfs(i, j, 3, 0, 1))

        return max_length
