class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def check_diagonal(grid, i, j, direction):
            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 1:
                return 0

            count = 1
            while True:
                i += direction[0]
                j += direction[1]

                if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 2:
                    break

                count += 1

            return count

        max_length = 0

        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, check_diagonal(grid, i, j, (0, 1)))
                    max_length = max(max_length, check_diagonal(grid, i, j, (1, 0)))
                    max_length = max(max_length, check_diagonal(grid, i, j, (1, 1)))
                    max_length = max(max_length, check_diagonal(grid, i, j, (-1, 1)))

        return max_length
