            while i + 1 < len(grid) and j - 1 >= 0:
                if grid[i + 1][j - 1] == 2:
                    count += 1
                    i += 1
                    j -= 1
                elif grid[i + 1][j - 1] == 0:
                    break
                else:
                    break

            return count

        max_length = 0

        for i in range(len(grid)):
            for j in range(len(grid[0])):
