class Solution:
    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7

        def count_valid_arrangements(m, n, k):
            if k == 0:
                return 1
            if k == 1:
                return m * n

            dp = [[0] * (k + 1) for _ in range(m * n + 1)]
            for i in range(m * n + 1):
                dp[i][1] = 1

            for i in range(2, k + 1):
                for j in range(m * n + 1):
                    for x in range(m * n + 1):
                        if dp[x][i - 1] > 0:
                            dp[j][i] = (dp[j][i] + dp[x][i - 1]) % MOD

            return dp[m * n][k]

        def calculate_distance_sum(m, n, k):
            total_distance = 0
            for i in range(m * n):
                for j in range(i + 1, m * n):
                    total_distance = (total_distance + abs(i // n - j // n) + abs(i % n - j % n)) % MOD
            return total_distance

        return (count_valid_arrangements(m, n, k) * calculate_distance_sum(m, n, k)) % MOD
