class Solution:
    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7

        def count_ways(m, n, k):
            if k == 0:
                return 1
            if k > m * n:
                return 0

            return (count_ways(m - 1, n, k - 1) + count_ways(m, n - 1, k - 1)) % MOD

        def calculate_distance(m, n, k):
            if k == 0:
                return 0

            return (k * (k - 1) // 2) * (m + n - 1) * (m + n - 2) // 2 % MOD

