class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_distances = [-1] * n
        index_map = {num: [] for num in set(nums)}

        for i, num in enumerate(nums):
            index_map[num].append(i)

        for i, num in enumerate(nums):
            for j in index_map[num]:
                if j == i:
                    continue
                if min_distances[j] == -1 or abs(i - j) < min_distances[j]:
                    min_distances[j] = abs(i - j)

        return [min_distances[q] for q in queries]
