class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * 5 for _ in range(k + 1)] for _ in range(n + 1)]

        def dfs(i, j, k):
            if i == n:
                return 0
            if dp[i][j][k] != -1:
                return dp[i][j][k]
            res = 0
            for d in range(4):
                if k > 0:
                    res = max(res, dfs(i + 1, j + d, k - 1) + abs(d - 2))
                else:
                    res = max(res, dfs(i + 1, j + d, k) + abs(d - 2))
            dp[i][j][k] = res
            return res

        return dfs(0, 0, k)
