class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        max_dist = 0
        current_dist = 0
        count = 0

        for i in range(len(s)):
            if s[i] == 'N':
                current_dist += 1
            elif s[i] == 'S':
                current_dist -= 1
            elif s[i] == 'E':
                current_dist += 1
            elif s[i] == 'W':
                current_dist -= 1

            if current_dist > max_dist:
                max_dist = current_dist

            if current_dist < 0:
                count += 1
                if count > k:
                    current_dist += 2 * current_dist
                    count = 0

        return max_dist
