class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * 5 for _ in range(k + 1)] for _ in range(n + 1)]

        def dfs(i, j, k):
            if i == n:
                return 0
            if dp[i][j][k] != -1:
                return dp[i][j][k]
            res = 0
            for d in range(4):
                if j + d < 0 or j + d > 4:
                    continue
                if k > 0:
                    res = max(res, dfs(i + 1, j + d, k - 1) + abs(i - n) + abs(j + d - 4))
