class Solution:
    def generateString(self, str1: str, str2: str) -> str:
        """
        Returns the lexicographically smallest possible string that can be generated by str1 and str2.
        If no string can be generated, returns an empty string.
        """
        n = len(str1)
        m = len(str2)
        result = [''] * (n + m - 1)

        i = 0
        j = 0

        while i < n or j < m:
            if i < n and (str1[i] == 'T' or (str1[i] == 'F' and str2 == '')):
                result[i + j] = 'a'
                i += 1
            elif j < m and (str1[i] == 'F' or (str1[i] == 'T' and str2 == '')):
                result[i + j] = 'b'
                j += 1
            else:
                if str2 == '':
                    return ''
                if str1[i] == 'T':
                    result[i + j] = str2[0]
                    str2 = str2[1:]
                else:
                    result[i + j] = str2[-1]
                    str2 = str2[:-1]
                i += 1

        return ''.join(result)
