                    char_count[ord(s[i]) - ord('a')] -= 1
                    char_count[ord(s[j]) - ord('a')] -= 1
                    if self.is_disjoint(s, i, j):
                        char_count[ord(s[i]) - ord('a')] += 1
                        char_count[ord(s[j]) - ord('a')] += 1
                        k -= 1
                        if k == 0:
