        def backtrack(start, current_sum, current_product, target, limit):
            if current_sum == target and current_product > max_product:
                max_product = current_product
                return
            if current_sum > target or current_product > max_product or current_sum < 0 or current_product < 0:
                return
            if start == len(nums):
                return
            backtrack(start + 1, current_sum + nums[start], current_product * nums[start], target, limit)
            backtrack(start + 1, current_sum, current_product, target, limit)

        max_product = float('-inf')
        backtrack(0, 0, 1, k, limit)
        return max_product if max_product != float('-inf') else -1
