class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers
        sorted in lexicographical order. If there are fewer than k valid alternating
        permutations, returns an empty list.
        """
        def is_valid_permutation(perm):
            """
            Checks if a permutation is valid (no two adjacent elements are both odd or both even).
            """
            for i in range(n - 1):
                if (perm[i] % 2 == perm[i + 1] % 2):
                    return False
            return True

        def generate_permutations(n, k):
            """
            Generates all alternating permutations of the first n positive integers and returns the k-th one.
            """
            def backtrack(perm, start):
                nonlocal k
                if k == 0:
                    return perm
                if start == n:
                    if is_valid_permutation(perm):
                        return perm
                    return []
                for i in range(start, n):
                    perm[start], perm[i] = perm[i], perm[start]
                    result = backtrack(perm, start + 1)
                    if result:
                        return result
                    perm[start], perm[i] = perm[i], perm[start]
                return []

            return backtrack([0] * n, 0)

        return generate_permutations(n, k)
