class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        """
        Returns an array answer, where answer[i] is the length of the longest common prefix among any k strings
        (selected at distinct indices) from the remaining array after removing the i^th element.
        If removing the i^th element leaves the array with fewer than k strings, answer[i] is 0.
        """
        def longest_common_prefix(words):
            """
            Returns the longest common prefix among the first two strings in the list.
            """
            if not words:
                return ""
            prefix = words[0]
            for word in words[1:]:
                while not word.startswith(prefix):
                    prefix = prefix[:-1]
                    if not prefix:
                        return ""
            return prefix

        def find_longest_common_prefix(words, k):
            """
            Returns an array answer, where answer[i] is the length of the longest common prefix among any k strings
            (selected at distinct indices) from the remaining array after removing the i^th element.
            If removing the i^th element leaves the array with fewer than k strings, answer[i] is 0.
            """
            if not words:
                return [0] * len(words)
            answer = [0] * len(words)
            for i in range(len(words)):
                if i + 1 < k:
                    answer[i] = len(longest_common_prefix(words[:i+1]))
                else:
                    answer[i] = 0
            return answer

        return find_longest_common_prefix(words, k)
