class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def lcp(s1, s2):
            """Returns the longest common prefix of two strings."""
            return s1[:min(len(s1), len(s2))].find(s2[:min(len(s1), len(s2))])

        def dfs(i, path, visited):
            """Recursively explores all possible combinations of k strings."""
            if len(path) == k:
                return
            for j in range(i, len(words)):
                if j not in visited:
                    visited.add(j)
                    for k in range(len(path)):
                        if lcp(words[path[k]], words[j]) == 0:
                            break
                    else:
                        dfs(j, path + [j], visited)
                    visited.remove(j)

        visited = set()
        dfs(0, [0], visited)
        return [lcp(words[path[0]], words[path[i]]) for i in range(1, len(path))]
