class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def lcp(a, b):
            """Return the longest common prefix of strings a and b."""
            n = min(len(a), len(b))
            for i in range(n):
                if a[i] != b[i]:
                    return a[:i]
            return a[:n]

        def dfs(i, seen, memo):
            """Recursively find the longest common prefix for each index i."""
            if i == len(words):
                return []
            if (i, tuple(seen)) in memo:
                return memo[(i, tuple(seen))]
            res = []
            for j in range(len(words)):
                if j not in seen:
                    res.append(lcp(words[i], words[j]))
                    seen.add(j)
            memo[(i, tuple(seen))] = res
            return res

        return dfs(0, set(), {})
