class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def is_common_prefix(word, prefix):
            return word.startswith(prefix)

        def find_longest_common_prefix(words, k, start, end):
            if start == end:
                return words[start]

            mid = (start + end) // 2
            left = find_longest_common_prefix(words, k, start, mid)
            right = find_longest_common_prefix(words, k, mid + 1, end)

            return find_common_prefix(left, right)

        def find_common_prefix(left, right):
            i = 0
            while i < len(left) and i < len(right) and left[i] == right[i]:
                i += 1
            return left[:i]

        result = []
        for i in range(len(words)):
            words_copy = words.copy()
            words_copy.pop(i)
            longest_common_prefix_length = 0
            for j in range(k):
                if len(words_copy) < k:
                    break
                longest_common_prefix = find_longest_common_prefix(words_copy, k, 0, len(words_copy) - 1)
                longest_common_prefix_length = max(longest_common_prefix_length, len(longest_common_prefix))
            result.append(longest_common_prefix_length)
        return result
