        """
        Returns an array answer, where answer[i] is the length of the longest common prefix among any k strings
        (selected at distinct indices) from the remaining array after removing the i^th element.
        If removing the i^th element leaves the array with fewer than k strings, answer[i] is 0.
        """
        def is_common_prefix(word1, word2):
            """
            Returns True if word1 and word2 have the same prefix of length len(word1).
            """
            return word1[:len(word1)] == word2[:len(word1)]

        def find_longest_common_prefix(words, k):
            """
            Returns the length of the longest common prefix among any k strings from the remaining array.
            """
            if len(words) < k:
                return 0
            prefix = words[0]
            for word in words[1:]:
                while not is_common_prefix(word, prefix):
                    prefix = prefix[:-1]
                    if not prefix:
                        return 0
            return len(prefix)

        return [find_longest_common_prefix(words[:i] + words[i+1:], k) for i in range(len(words))]
