            if (i, tuple(seen)) in memo:
                return memo[(i, tuple(seen))]
            res = []
            for j in range(len(words)):
                if j not in seen:
                    res.append(lcp(words[i], words[j]))
                    seen.add(j)
            memo[(i, tuple(seen))] = res
            return res

        return dfs(0, set(), {})

class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def is_common_prefix(word, prefix):
            return word.startswith(prefix)

        def find_longest_common_prefix(words, k, start, end):
            if start == end:
                return words[start]

            mid = (start + end) // 2
            left = find_longest_common_prefix(words, k, start, mid)
            right = find_longest_common_prefix(words, k, mid + 1, end)

            return find_common_prefix(left, right)

        def find_common_prefix(left, right):
            i = 0
            while i < len(left) and i < len(right) and left[i] == right[i]:
                i += 1
            return left[:i]

        result = []
        for i in range(len(words)):
            words_copy = words.copy()
            words_copy.pop(i)
            longest_common_prefix_length = 0
            for j in range(k):
                if len(words_copy) < k:
                    break
                longest_common_prefix = find_longest_common_prefix(words_copy, k, 0, len(words_copy) - 1)
                longest_common_prefix_length = max(longest_common_prefix_length, len(longest_common_prefix))
            result.append(longest_common_prefix_length)
        return result

class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        """
        Returns an array answer, where answer[i] is the length of the longest common prefix among any k strings
        (selected at distinct indices) from the remaining array after removing the i^th element.
        If removing the i^th element leaves the array with fewer than k strings, answer[i] is 0.
        """
        def is_common_prefix(word1, word2):
            """
            Returns True if word1 and word2 have the same prefix of length len(word1).
            """
            return word1[:len(word1)] == word2[:len(word1)]

        def find_longest_common_prefix(words, k):
            """
            Returns the length of the longest common prefix among any k strings from the remaining array.
            """
            if len(words) < k:
                return 0
            prefix = words[0]
            for word in words[1:]:
                while not is_common_prefix(word, prefix):
                    prefix = prefix[:-1]
                    if not prefix:
                        return 0
            return len(prefix)

        return [find_longest_common_prefix(words[:i] + words[i+1:], k) for i in range(len(words))]

class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def lcp(a, b):
            n = min(len(a), len(b))
            for i in range(n):
                if a[i] != b[i]:
                    return a[:i]
            return a[:n]

        def dfs(i, path, visited):
            if len(visited) == k:
                return
            if i == len(words):
                self.res.append(path)
                return
            for j in range(len(words)):
                if j not in visited:
                    visited.add(j)
                    dfs(i + 1, path + [words[j]], visited)
                    visited.remove(j)

        self.res = []
        dfs(0, [], set())
        return [lcp(*self.res[i]) for i in range(len(self.res))]

