class Solution:
    def countArrays(self, original: List[int], bounds: List[List[int]]) -> int:
        """
        :type original: List[int]
        :type bounds: List[List[int]]
        :rtype: int
        """
        def check(arr):
            for i in range(1, len(arr)):
                if arr[i] - arr[i - 1] != arr[1] - arr[0]:
                    return False
            return True

        n = len(original)
        dp = [0] * (1 << n)
        dp[0] = 1

        for mask in range(1, 1 << n):
            for i in range(n):
                if mask & (1 << i):
                    for u, v in bounds:
                        if u <= original[i] <= v:
                            new_mask = mask ^ (1 << i)
                            if check(original[i] + [original[j] for j in range(n) if (mask & (1 << j))]):
                                dp[new_mask] += dp[mask]

        return dp[(1 << n) - 1]
