class Solution:
    def longestPalindromicSubsequence(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[0] * (k + 1) for _ in range(n)]

        for i in range(n):
            dp[i][0] = 1

        for i in range(1, n):
            for j in range(1, k + 1):
                if s[i] == s[i - 1]:
                    dp[i][j] = dp[i - 1][j - 1] + 2
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

        return dp[n - 1][k]
