class Solution:
    def maxSubarrays(self, n: int, conflictingPairs: List[List[int]]) -> int:
        """
        :type n: int
        :type conflictingPairs: List[List[int]]
        :rtype: int
        """
        # Create a set of all numbers from 1 to n
        nums = set(range(1, n + 1))

        # Create a set of all conflicting pairs
        conflicting_pairs = set(map(tuple, conflictingPairs))

        # Create a set of all pairs that can be removed
        removable_pairs = set()
        for a, b in conflicting_pairs:
            if a > 1 and (a - 1, a) not in conflicting_pairs:
                removable_pairs.add((a - 1, a))
            if b < n and (b, b + 1) not in conflicting_pairs:
                removable_pairs.add((b, b + 1))

        # Initialize the maximum number of subarrays
        max_subarrays = 0

        # Try removing each removable pair and count the number of subarrays
        for a, b in removable_pairs:
            remaining_nums = nums - {(a, b)}
            remaining_conflicting_pairs = conflicting_pairs - {(a, b)}
            subarrays = 0
            for i in range(1, n + 1):
                for j in range(i, n + 1):
                    if (i, j) not in remaining_conflicting_pairs:
                        subarrays += 1
            max_subarrays = max(max_subarrays, subarrays)

        return max_subarrays
