class Solution:
    def minTime(self, skill: List[int], mana: List[int]) -> int:
        n = len(skill)
        m = len(mana)
        dp = [[0] * (m + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
            for j in range(1, m + 1):
                dp[i][j] = dp[i - 1][j]
                for k in range(j):
                    dp[i][j] = min(dp[i][j], dp[i - 1][k] + skill[i - 1] * mana[j - 1])

        return dp[n][m]

