class Solution:
    def maxActiveSectionsAfterTrade(self, s: str) -> int:
        """
        :type s: str
        :rtype: int
        """
        s = '1' + s + '1'
        n = len(s)
        dp = [0] * n
        for i in range(1, n - 1):
            if s[i - 1] == '0' and s[i] == '1' and s[i + 1] == '0':
                dp[i] = dp[i - 1] + 1
            elif s[i - 1] == '1' and s[i] == '0' and s[i + 1] == '1':
                dp[i] = dp[i - 1] + 1
            else:
                dp[i] = dp[i - 1]
        return max(dp)
