from collections import defaultdict
from math import gcd
from functools import reduce

def prime_factors(n):
    """Return a list of prime factors of n."""
    i = 2
    factors = []
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)
    return factors

def totient(n, p):
    """Return the number of integers up to n that are coprime with n."""
    factors = prime_factors(n)
    return reduce(lambda x, y: x * y, (1 - 1 / p**f for p, f in zip(factors, factors)), 1)

def count_even_odd_distances(n, p):
    """Count the number of undirected connected simple graphs with n vertices and M edges
    that satisfy the condition: the number of vertices whose shortest distance from vertex 1
    is even is equal to the number of vertices whose shortest distance from vertex 1 is odd.
    Return this number modulo p."""
    if n % 2 != 0:
        raise ValueError("N must be even")

    M = (n * (n - 1)) // 2
    totients = [totient(n, p) for _ in range(M)]
    even_count = 0
    odd_count = 0

    for i in range(M):
        if totients[i] % 2 == 0:
            even_count += 1
        else:
            odd_count += 1

    return even_count % p
