from collections import defaultdict
from math import gcd
from itertools import combinations

def sieve_of_eratosthenes(limit):
    """
    Generate a list of prime numbers up to a given limit using the Sieve of Eratosthenes algorithm.
    """
    is_prime = [True] * (limit + 1)
    p = 2
    while (p * p <= limit):
        if (is_prime[p] == True):
            for i in range(p * p, limit + 1, p):
                is_prime[i] = False
        p += 1
    return [p for p in range(2, limit + 1) if is_prime[p]]

def prime_factors(n):
    """
    Return a list of prime factors of a given number n.
    """
    factors = []
    for p in sieve_of_eratosthenes(int(n**0.5) + 1):
        while n % p == 0:
            factors.append(p)
            n //= p
    if n > 1:
        factors.append(n)
    return factors

def euler_totient(n, primes):
    """
    Calculate Euler's Totient function for a given number n using the prime factorization.
    """
    result = n
    for p in primes:
        if n % p == 0:
            result -= result // p
            while n % p == 0:
                n //= p
    if n > 1:
        result -= result // n
    return result

def count_connected_graphs(N, M, P):
    """
    Count the number of connected undirected simple graphs with N vertices and M edges
    that satisfy the condition: the number of vertices whose shortest distance from vertex 1
    is even is equal to the number of vertices whose shortest distance from vertex 1 is odd.
    """
    if M > (N * (N - 1)) // 2:
        return 0

    primes = prime_factors(P)
    totient = euler_totient(P, primes)

    count = 0
    for m in range(M, 0, -1):
        if m % 2 == 0:
            count = (count * totient) % P
        else:
            count = (count * totient * (totient - 1)) % P

    return count
